/* Global Styles */
    :global(body) {
        margin: 0;
        padding: 0;
        font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        background-color: #f4f7f6;
        color: #333;
        line-height: 1.6;
    }

    /* RTL Support */
    .rtl {
        direction: rtl;
        text-align: right;
    }

    .ltr {
        direction: ltr;
        text-align: left;
    }

    /* Container */
    .container {
        max-width: 1200px;
        margin: 2rem auto;
        padding: 1rem;
    }

    /* Header */
    header {
        text-align: center;
        margin-bottom: 2rem;
    }

    h1 {
        font-size: 2.8rem;
        color: #2c3e50;
        margin-bottom: 0.5rem;
    }

    .game-night-description {
        font-size: 1.1rem;
        color: #555;
        margin-bottom: 1.5rem;
    }

    /* Language Toggle Button */
    .language-toggle {
        background-color: #3498db;
        color: white;
        border: none;
        padding: 0.75rem 1.5rem;
        border-radius: 5px;
        cursor: pointer;
        font-size: 1rem;
        transition: background-color 0.3s ease;
        margin-top: 1rem;
    }

    .language-toggle:hover {
        background-color: #2980b9;
    }

    .language-toggle:focus {
        outline: 2px solid #2980b9;
        outline-offset: 2px;
    }

    /* WhatsApp Button */
    .whatsapp-button {
        display: inline-flex;
        align-items: center;
        background-color: #25d366;
        color: white;
        text-decoration: none;
        padding: 0.75rem 1.5rem;
        border-radius: 5px;
        margin: 1rem auto;
        font-size: 1rem;
        transition: background-color 0.3s ease;
    }

    .whatsapp-button:hover {
        background-color: #128c7e;
    }

    .whatsapp-icon {
        width: 24px;
        height: 24px;
        margin-right: 0.75rem;
    }

    /* Games Container */
    .games-container {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
        gap: 1.5rem;
    }

    /* Game Buttons */
    .game-button {
        background-color: white;
        border: 1px solid #e0e0e0;
        border-radius: 8px;
        padding: 2rem 1rem;
        font-size: 1.25rem;
        cursor: pointer;
        transition: all 0.3s ease;
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        width: 100%;
        text-align: center;
    }

    .game-button:hover {
        transform: translateY(-5px);
        box-shadow: 0 8px 16px rgba(0, 0, 0, 0.15);
        border-color: #3498db;
    }

    .game-button:focus {
        outline: 2px solid #3498db;
        outline-offset: 2px;
        transform: translateY(-5px);
        box-shadow: 0 8px 16px rgba(0, 0, 0, 0.15);
        border-color: #3498db;
    }

    /* Modal Overlay */
    .modal-overlay {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background-color: rgba(0, 0, 0, 0.5);
        display: flex;
        justify-content: center;
        align-items: center;
        z-index: 1000;
    }

    /* Modal */
    .modal {
        background-color: white;
        border-radius: 10px;
        width: 90%;
        max-width: 600px;
        max-height: 90vh;
        overflow-y: auto;
        box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
    }

    /* Modal Header */
    .modal-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 1.5rem;
        border-bottom: 1px solid #e0e0e0;
    }

    .modal-header h2 {
        margin: 0;
        color: #2c3e50;
    }

    /* Close Button */
    .close-button {
        background: none;
        border: none;
        font-size: 1.5rem;
        cursor: pointer;
        color: #7f8c8d;
        transition: color 0.3s ease;
        padding: 0.5rem;
        line-height: 1;
        border-radius: 4px;
    }

    .close-button:hover {
        color: #e74c3c;
    }

    .close-button:focus {
        outline: 2px solid #e74c3c;
        color: #e74c3c;
    }

    /* Modal Content */
    .modal-content {
        padding: 1.5rem;
    }

    .modal-content section {
        margin-bottom: 1.5rem;
    }

    .modal-content h3 {
        color: #2c3e50;
        margin-top: 0;
        margin-bottom: 0.75rem;
    }

    /* List Styles */
    ol, ul {
        padding-left: 1.5rem;
        margin-top: 0.5rem;
    }

    .rtl ol, .rtl ul {
        padding-right: 1.5rem;
        padding-left: 0;
    }

    li {
        margin-bottom: 0.5rem;
    }

    /* Responsive Adjustments */
    @media (max-width: 768px) {
        .container {
            padding: 1rem;
        }

        h1 {
            font-size: 2.2rem;
        }

        .games-container {
            grid-template-columns: 1fr;
        }
    }

